<?php namespace App\Http\Controllers; use App\Http\Controllers\Controller; use Illuminate\Http\Request; use App\Models\BookType; class BookTypeController extends Controller { public function index() { $book_types = BookType::all(); $list = true;return view('book_types.index', compact('book_types', 'list')); } public function create() { return view('book_types.create',); } public function store(Request $request) { $this->validate($request, [ 'name' => 'required', ]); $booktype = new BookType(); $booktype->fill($request->all()); $booktype->save(); return redirect()->route('book_types.index'); } public function show(BookType $booktype) { return view('book_types.show', compact('booktype',)); } public function edit(BookType $booktype) { return view('book_types.edit', compact('booktype')); } public function update(Request $request, BookType  $booktype) { $this->validate($request, [ 'name' => 'required', ]); $booktype->name = $request->name; $booktype->save(); session()->flash('message', 'Record updated successfully.'); return redirect()->route('book_types.edit' , $booktype->id); } public function destroy(BookType $booktype) { $booktype->delete(); session()->flash('success', 'Deleted Successfully'); return redirect()->route('book_types.index'); } } ?>