<?php namespace App\Http\Controllers; use App\Http\Controllers\Controller; use Illuminate\Http\Request; use App\Models\ExamType; class ExamTypeController extends Controller { public function index() { $exam_types = ExamType::all(); $list = true;return view('exam_types.index', compact('exam_types', 'list')); } public function create() { return view('exam_types.create',); } public function store(Request $request) { $this->validate($request, [ 'name' => 'required', ]); $examtype = new ExamType(); $examtype->fill($request->all()); $examtype->save(); return redirect()->route('exam_types.index'); } public function show(ExamType $examtype) { return view('exam_types.show', compact('examtype',)); } public function edit(ExamType $examtype) { return view('exam_types.edit', compact('examtype')); } public function update(Request $request, ExamType  $examtype) { $this->validate($request, [ 'name' => 'required', ]); $examtype->name = $request->name; $examtype->save(); session()->flash('message', 'Record updated successfully.'); return redirect()->route('exam_types.edit' , $examtype->id); } public function destroy(ExamType $examtype) { $examtype->delete(); session()->flash('success', 'Deleted Successfully'); return redirect()->route('exam_types.index'); } } ?>