<?php namespace App\Http\Controllers; use App\Http\Controllers\Controller; use Illuminate\Http\Request; use App\Models\FeeType; class FeeTypeController extends Controller { public function index() { $fee_types = FeeType::all(); $list = true;return view('fee_types.index', compact('fee_types', 'list')); } public function create() { return view('fee_types.create',); } public function store(Request $request) { $this->validate($request, [ 'name' => 'required', ]); $feetype = new FeeType(); $feetype->fill($request->all()); $feetype->save(); return redirect()->route('fee_types.index'); } public function show(FeeType $feetype) { return view('fee_types.show', compact(' feetype',)); } public function edit(FeeType $feetype) { return view('fee_types.edit', compact('feetype')); } public function update(Request $request, FeeType  $feetype) { $this->validate($request, [ 'name' => 'required', ]); $feetype->name = $request->name; $feetype->save(); session()->flash('success', 'Record updated successfully.'); return redirect()->route('fee_types.edit' , $feetype->id); } public function destroy(FeeType $feetype) { $feetype->delete(); return redirect()->route('fee_types.index'); session()->flash('success', 'Deleted Successfully'); } } ?>