<?php namespace App\Http\Controllers; use App\Http\Controllers\Controller; use Illuminate\Http\Request; use App\Models\LibraryMemberType; class LibraryMemberTypeController extends Controller { public function index() { $library_member_types = LibraryMemberType::all(); $list = true;return view('library_member_types.index', compact('library_member_types', 'list')); } public function create() { return view('library_member_types.create',); } public function store(Request $request) { $this->validate($request, [ 'name' => 'required', ]); $librarymembertype = new LibraryMemberType(); $librarymembertype->fill($request->all()); $librarymembertype->save(); return redirect()->route('library_member_types.index'); } public function show(LibraryMemberType $librarymembertype) { return view('library_member_types.show', compact('librarymembertype',)); } public function edit(LibraryMemberType $librarymembertype) { return view('library_member_types.edit', compact('librarymembertype')); } public function update(Request $request, LibraryMemberType  $librarymembertype) { $this->validate($request, [ 'name' => 'required', ]); $librarymembertype->name = $request->name; $librarymembertype->save(); session()->flash('message', 'Record updated successfully.'); return redirect()->route('library_member_types.edit' , $librarymembertype->id); } public function destroy(LibraryMemberType $librarymembertype) { $librarymembertype->delete(); session()->flash('success', 'Deleted Successfully'); return redirect()->route('library_member_types.index'); } } ?>