<?php namespace App\Http\Controllers; use App\Http\Controllers\Controller; use Illuminate\Http\Request; use App\Models\Shift; class ShiftController extends Controller { public function index() { $shifts = Shift::all(); $list = true;return view('shifts.index', compact('shifts', 'list')); } public function create() { return view('shifts.create',); } public function store(Request $request) { $this->validate($request, [ 'name' => 'required', ]); $shift = new Shift(); $shift->fill($request->all()); $shift->save(); return redirect()->route('shifts.index'); } public function show(Shift $shift) { return view('shifts.show', compact(' shift',)); } public function edit(Shift $shift) { return view('shifts.edit', compact('shift')); } public function update(Request $request, Shift  $shift) { $this->validate($request, [ 'name' => 'required', ]); $shift->name = $request->name; $shift->save(); session()->flash('success', 'Record updated successfully.'); return redirect()->route('shifts.edit' , $shift->id); } public function destroy(Shift $shift) { $shift->delete(); return redirect()->route('shifts.index'); session()->flash('success', 'Deleted Successfully'); } } ?>