<?php namespace App\Http\Controllers; use App\Http\Controllers\Controller; use Illuminate\Http\Request; use App\Models\StudentAddress; class StudentAddressController extends Controller { public function index() { $student_addresses = StudentAddress::all(); $list = true;return view('student_addresses.index', compact('student_addresses', 'list')); } public function create() { return view('student_addresses.create',); } public function store(Request $request) { $this->validate($request, [ 'area' => 'required', ]); $studentaddress = new StudentAddress(); $studentaddress->fill($request->all()); $studentaddress->save(); return redirect()->route('student_addresses.index'); } public function show(StudentAddress $studentaddress) { return view('student_addresses.show', compact('studentaddress',)); } public function edit(StudentAddress $studentaddress) { return view('student_addresses.edit', compact('studentaddress')); } public function update(Request $request, StudentAddress  $studentaddress) { $this->validate($request, [ 'area' => 'required', ]); $studentaddress->area = $request->area; $studentaddress->save(); session()->flash('message', 'Record updated successfully.'); return redirect()->route('student_addresses.edit' , $studentaddress->id); } public function destroy(StudentAddress $studentaddress) { $studentaddress->delete(); session()->flash('success', 'Deleted Successfully'); return redirect()->route('student_addresses.index'); } } ?>